import React from "react";
import axios from 'axios';
// import { useRouter } from 'next/router'
// import SEO from "../components/seo"
import '../scss/index.scss';


class IndexPage extends React.Component {
  // const router = useRouter()
  // const { tweeturl } = router.query
  constructor(props) {
    super(props);
    this.state = {
        url: '',
    };

    this.handleChange = this.handleChange.bind(this);
    this.handleSubmit = this.handleSubmit.bind(this);
  }

  handleChange(e){
    e.preventDefault();
    this.setState({
        url: e.target.value,
    });
  }

  handleSubmit(e){
    e.preventDefault();
    //making a post request with the fetch API
    axios.post('/api', {
      url: this.state.url
    })
    .then(response => {
      // console.log(response.data)
      const div = document.getElementById('tweet');
      div.innerHTML = response.data;
      clean(div)
    })
    .catch(error => console.log(error))
  }

  clean(tweet){
    console.log(document.getElementsByClassName('js-tweet-text-container')[0])
  }

  render(){
    return (
      <div id='container'>
        <h1>tweet-img</h1>
        <form onSubmit={this.handleSubmit}>
          <label>Tweet URL</label>
          <input id='url-input'type='text' onChange={this.handleChange} name='url' placeholder='twitter.com/tweeturl'/>
          <input type="submit" value="Submit"/>
        </form>

        <div id='tweet'></div>

      </div>
    );
  }
}

// IndexPage.getInitialProps = async ({ req }) => {
//   const res = await axios.post('/');
//   const json = await res.json();
//   return { tweeturl: json.url };
// }

export default IndexPage
